/* ==UserStyle==
@name           help.obsidian.md - Feb 2024
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A new userstyle
@author         Me
==/UserStyle== */

@-moz-document domain("help.obsidian.md") {
    /* Obsidian Help - Catppuccin Mocha theme */
    /* Site Left section colors */
    .site-body-left-column {
        background-color: #181825 !important;
        color: #cdd6f4 !important;
        border-right: 1px solid #b4befe !important;
    }
    /* Compact View Title bar */
    .published-container.has-navigation .site-header {
        background-color: #181825 !important;
    }
    /* Site left section item text color */
    .tree-item-inner {
        color: #b4befe !important;
    }
    /* Site left section item text color on hover*/
    .tree-item-inner:hover {
        color: #a6e3a1!important;
    }
    /* collapse arrow icon color */
    .collapse-icon {
        color: #fab387 !important;
    }
    /* Unhovered item left line/border colour */
    .nav-view-outer .tree-item-self:not(.mod-collapsible) {
        border-left: 1px solid #fab387;
    }
    /* Hovered item left line/border color */
    .nav-view-outer .tree-item-self:not(.mod-collapsible):hover {
        border-left: 1px solid #a6e3a1 !important;
    }
    /* logo colour light */
    .site-body-left-column-site-logo {
        filter: none !important;
    }
    /* Compact view logo color */
    .theme-light .site-header-logo, .theme-light .site-body-left-column-site-logo {
        filter: invert(30%) hue-rotate(330deg) opacity(90%) !important;
    }
    /* Compact View header */
    .site-header {
        border-bottom: 1px solid #b4befe !important;
    }
    /* site menu click color */
    .published-container.has-navigation .site-header .clickable-icon {
        color: #b4befe !important;
    }
    /* site menu icon hover color */
    .published-container.has-navigation .site-header .clickable-icon:hover {
        color: #fab387 !important;
    }
    /* Search bar color */
    .search-view-container .search-bar{
        background-color: #313244 !important;
    }
    /* Searchbar properties */
    textarea, input.metadata-input-text, input[type="date"], input[type="datetime-local"], input[type="text"], input[type="search"], input[type="email"], input[type="password"], input[type="number"] {
	border: var(--input-border-width) solid #b4befe !important;
	color: #b4befe !important;
	border-radius: 10px !important;
    }
    /* Search bar icon color */
    .published-search-icon {
        color: #b4befe !important;
    }
    /* Search result colors */
    .search-results {
        background-color: rgba(49, 50, 68) !important;
        color: rgba(205, 214, 244) !important;
    }
    /* Search result, suggestion mini title */
    .suggestion-item.mod-complex .suggestion-note {
        color: #b4befe !important;
    }
    /* Search bar place holder */
    .search-view-container .search-bar::placeholder {
        color: rgba(180, 190, 254) !important;
    }
    /* Theme selector button color */
    .site-body-left-column-site-theme-toggle .checkbox-container::after {
        background-color: #fab387 !important;
    }
    /* Theme selector background color */
    .checkbox-container {
        background-color: #313244 !important;
    }
    .site-body-left-column-site-theme-toggle .checkbox-container {
        box-shadow: inset 0 0 0 1px #89b4fa !important;
    }
    /* Main text background */
    .markdown-preview-view {
        background-color: #1e1e2e !important;
        color: #bac2de !important;
    }
    /* Table header colors */
    .markdown-rendered th {
        color: #89b4fa !important;
    }
    /* Table border color */
    table, tr, td, th {
        border: 1px inset #7f849c !important;
    }
    /* Inline code background colour */
    code {
        background-color: #585b70 !important;
        color: #cdd6f4 !important;
    }
    /* Hide powered by Obsidian publish */
    .site-footer {
        display: none !important;
    }
    /* Link Colors */
    a {
        color: #89b4fa !important;
    }
    /* Section title properties */
    h2 {
        color: #b4befe !important;
        border-bottom: 1px solid #b4befe !important;
    }
    /* Small section title */
    h3 {
        color: #b4befe !important;
    }
    /* Page title color */
    .page-header {
        color: #b4befe !important;
    }
    /* Bottom link color */
    .published-section-header {
        color: #b4befe !important;
    }
    .callout {
        background-color: rgba(49, 50, 68, 0.7) !important;
        mix-blend-mode: normal !important;
        border-radius: 15px !important;
    }
    /* Callout title */
    .callout-title-inner, .callout-icon .svg-icon {
        color: #a6e3a1 !important;
    }
    /* bottom horizontal line color */
    .backlinks {
        border-top: 1px solid #b4befe;
    }
    /* Variables*/
    body {
        --callout-color: #969bc0 !important;
        --callout-blend-mode: normal !important;
    }
    }

/* ==UserStyle==
@name           www.reddit.com - Feb 2024
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A new userstyle
@author         Me
==/UserStyle== */

@-moz-document domain("www.reddit.com") {
    /* Catppuccin Reddit */
      /* Header Background */
    .bg-neutral-background {
        background-color: #181825;
    }
    /* Reddit Logo Color */
    .snoo-cls-10 {
        fill: #768eae;
    }
    /* Header Search bar */
    .bg-secondary-background {
        background-color: #45475a;
    }
    :root:not(.theme-light) .theme-rpl, :root:not(.theme-light).theme-rpl {
        /* Card Background */
        --color-neutral-background: #1e1e2e;
        --color-neutral-background-hover: #313244;
        /* Main page button background */
        --color-secondary-background: #45475a;
        --color-secondary-background-hover: #585b70;
        /* Side bar selected colour */
        --color-neutral-background-selected: #313244;
        /* Side recent posts */
        --color-neutral-background-weak: #1e1e2e;
    }
    :root:not(.theme-light) .theme-beta, :root:not(.theme-light).theme-beta {
        /* search bar color */
        --color-secondary-background: #1e1e2e !important;
        --color-neutral-background-strong: #2d2d42;
        --color-input-plain: #2d2d42;
    }
    /* Background color under the cards */
    :root.theme-beta, :root.theme-rpl{
        background-color: #11111b;
    }
    /* Separation between the cards */
    .border-b-neutral-border-weak {
        border: 0px;
    }
    .bg-\[color\:var\(--shreddit-content-background\)\] {
        background-color: #1e1e2e;
    }

}

