        color: #cdd6f4 !important;
    }
    /* Page title colour */
    textarea {
        color: #cba6f7 !important;
    }
    /* day colors */
    .text-lime-500 {
        color: #a6e3a1; 
    }
    .text-red-500 {
        color: #f38ba8;
    }
    .text-state-active {
        color: #89b4fa;
    }
    /* calender red colour */
    :is(.dark .dark\:text-red-400) {
        color: #f38ba8;
    }
}

/* ==UserStyle==
@name           notes-wiki.vercel.app - Feb 2024
